import sys

# Given an array nums of n integers, are there elements a, b, c in nums such that a + b + c = 0?
# Find all unique triplets in the array which gives the sum of zero.


class Solution:
    # @param A : list of integers
    # @param B : integer
    # @return an integer
    def threeSum(self, nums):
        """
        :type nums: List[int]
        :rtype: List[List[int]]
        """
        if len(nums) < 3:
            return []
        result = []
        nums.sort()
        i = 0
        while i < len(nums)- 1:
            target = - nums[i]
            start_ptr = i + 1
            end_ptr = len(nums)- 1
            while start_ptr < end_ptr:
                sum = nums[start_ptr] + nums[end_ptr]
                if sum < target:
                    start_ptr += 1
                elif sum > target:
                    end_ptr -=1
                else:
                    result_triplet = [nums[i], nums[start_ptr], nums[end_ptr]]
                    result.append(result_triplet)
                    while start_ptr < end_ptr and nums[start_ptr] == result_triplet[1]:
                        start_ptr +=1
                    while start_ptr < end_ptr and nums[end_ptr] == result_triplet[2]:
                        end_ptr -=1
            while i+1 < len(nums)-1 and nums[i+1] == nums[i]:
                i = i+1
            i+=1
        return  result



    def threeSum2(self, nums):
        """
        :type nums: List[int]
        :rtype: List[List[int]]
        """
        if len(nums) < 3:
            return []
        nums.sort()
        result = set()
        numberDict = dict()
        for i in range(len(nums)):
            if nums[i] not in numberDict:
                numberDict[nums[i]] = [i]
            else:
                numberDict[nums[i]].append(i)

        for i in range(len(nums)):
            twoSumtarget = -nums[i]
            for j in range(i, len(nums)):
                if j == i:
                    continue
                twoSumSubtarget = twoSumtarget - nums[j]
                if twoSumSubtarget in numberDict :
                    # print "first:", nums[i], i, " second:", twoSumSubtarget, numberDict[twoSumSubtarget], " third:", \
                    #     nums[j], j,
                    # print "\n"
                    if len(numberDict[twoSumSubtarget]) > 2:
                        newresultset = [nums[i], nums[j], twoSumSubtarget]
                        newresultset.sort()
                        newresultset = tuple(newresultset)
                        result.add(newresultset)
                        continue
                    if len(numberDict[twoSumSubtarget]) == 1 and (numberDict[twoSumSubtarget][0] == j
                                                                  or numberDict[twoSumSubtarget][0] == i):
                        continue
                    else:
                        # print "first:", nums[i], i, " second:", twoSumSubtarget, numberDict[twoSumSubtarget], " third:", \
                        #     nums[j], j,
                        # print "\n"
                        not_duplicate = False
                        used_index = [i,j]
                        if len(numberDict[twoSumSubtarget]) > 2:
                            not_duplicate = True
                        else:
                            for index in numberDict[twoSumSubtarget]:
                                if index not in used_index:
                                    not_duplicate = True
                                    break
                        if not_duplicate == False:
                            continue
                        #

                        newresultset = [nums[i], nums[j], twoSumSubtarget]
                        newresultset.sort()
                        newresultset = tuple(newresultset)
                        result.add(newresultset)
        finalResult = []
        for triplet in result:
            finalResult.append(list(triplet))
        return finalResult

